import React, { useState } from "react";

function Experience() {
    const [position, setPosition] = useState("");
    const [company, setCompany] = useState("");
    const [from, setFrom] = useState("");
    const [to, setTo] = useState("");
    let prevExperience = [];

    function addExperience() {
        prevExperience[0] = position;
        prevExperience[1] = company;
        prevExperience[2] = from;
        prevExperience[3] = to;
    }

    return (
        <div className="add-experience">
            <h1> Experience </h1>
            <input onChange={(e) => {setPosition(e.target.value)}} className="exp" type="text" placeholder="Position" value={position}/>
            <input onChange={(e) => {setCompany(e.target.value)}} className="exp" type="text" placeholder="Company" value={company}/>
            <input onChange={(e) => {setFrom(e.target.value)}} className="exp" type="text" placeholder="From" value={from}/>
            <input onChange={(e) => {setTo(e.target.value)}} className="exp" type="text" placeholder="To" value={to}/>
            <button> Add </button>
        </div>
    )
}

export default Experience;
