import React, { useState, useEffect } from "react";

const Skills = ({ setSkills }) => {
    const [programmingLanguages, setProgrammingLanguages] = useState("");
    const [technicalSkills, setTechnicalSkills] = useState("");
    const [languagesSpoken, setLanguagesSpoken] = useState("");
    const skillsCopy = [programmingLanguages, technicalSkills, languagesSpoken];

    useEffect(() => {
        setSkills(skillsCopy);
    })
    return (
        <div className="skills">
            <h1>Skills</h1>
            <input onChange={(e) => {setProgrammingLanguages(e.target.value)}} type="text" placeholder="Programming Languages"/>
            <input onChange={(e) => {setTechnicalSkills(e.target.value)}} type="text" placeholder="Technical Skills"/>
            <input onChange={(e) => {setLanguagesSpoken(e.target.value)}} type="text" placeholder="Languages Spoken"/>
        </div>
    )
}

export default Skills;
