import React, { Component } from "react";

//<input onChange={this.infoChange} className="info" type="text" placeholder="First Name" id="first-name" value={this.info.firstName}/>
//<input onChange={this.infoChange} className="info" type="text" placeholder="Last Name" id="last-name" value={this.info.lastName}/>
//<input onChange={this.infoChange} className="info" type="text" placeholder="Address" id="address" value={this.info.address}/>
//<input onChange={this.infoChange} className="info" type="text" placeholder="Phone Number" id="phone-number" value={this.info.phoneNumber}/>
//<input onChange={this.infoChange} className="info" type="text" placeholder="Email" id="email" value={this.info.email}/>
//<input onChange={this.infoChange} className="info" type="text" placeholder="Description" id="description" value={this.info.description}/>


class General extends Component {
    constructor() {
        super();
        this.state = {
            info : {
                firstName: '',
                lastName: '',
                address: '',
                phoneNumber: '',
                email: '',
                description: '',
            }
        }
    }

    infoChange = (e) => {
        e.preventDefault();
        const whatInfo = e.target.id;
        this.setState({
            info: {
                whatInfo: e.target.value,
            }
        })
        console.log(info.firstName)
    }

    render() {
        const { info } = this.state;
        return (
            <div className="generalInformation">
                <h1> Personal Information </h1>
                <input onChange={this.infoChange} className="info" type="text" placeholder="First Name" id="firstName" value={info.firstName}/>
                <input onChange={this.infoChange} className="info" type="text" placeholder="Last Name" id="lastName" value={info.lastName}/>
                <input onChange={this.infoChange} className="info" type="text" placeholder="Address" id="address" value={info.address}/>
                <input onChange={this.infoChange} className="info" type="text" placeholder="Phone Number" id="phoneNumber" value={info.phoneNumber}/>
                <input onChange={this.infoChange} className="info" type="text" placeholder="Email" id="email" value={info.email}/>
                <input onChange={this.infoChange} className="info" type="text" placeholder="Description" id="description" value={info.description}/>
                <button></button>
            </div>
        )
    }

}

export default General;
